ItemDTO getItemDTO(int id){
  for (final e in allCharacter) {
    if(e.vid == id) return e;
  }
  for(final e in allWeapon){
    if(e.vid == id) return e;
  }
  for(final e in allMaterial){
    if(e.vid == id) return e;
  }
  return iNull;
}
